DROP TABLE IF EXISTS `yoho_order`;
CREATE TABLE `yoho_order` (
  `oid` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',
  `ordernum` varchar(45) NOT NULL DEFAULT '' COMMENT '订单号',
  `consignee` varchar(45) NOT NULL DEFAULT '' COMMENT '收货人',
  `address` varchar(255) NOT NULL DEFAULT '' COMMENT '地址',
  `tprice` decimal(7,3) NOT NULL DEFAULT '0.000' COMMENT '价格总计',
  `addtime` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '生成时间',
  `remark` varchar(45) NOT NULL DEFAULT '' COMMENT '备注',
  `state` enum('未付款','待发货','已发货','已完成') DEFAULT NULL COMMENT '状态',
  `user_id` int(10) unsigned NOT NULL COMMENT '用户id',
  PRIMARY KEY (`oid`),
  KEY `fk_order_user1_idx` (`user_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='订单表';

